package JavaTest_4;

public class ForFor99 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int x=1; x<=9 ;x++ )
		{
			for (int y=1; y<=x ;y++ )
			{
				System.out.print(y+"*"+x+"="+y*x+"\t");
			}
			System.out.println();
		}
		
		System.out.println("\\hello world\\");
		
	}

}
